import { useState } from "react"
const useWordle = (solution) =>{
    const[turn, setTurn] = useState(0)
    const[currentGuess, setCurrentGuess] = useState("")
    const[guesses, setGuesses] = useState([...Array(6)])
    const[history, setHistory] = useState([])
    const[isCorrect, setIsCorrect] = useState(false)
    const[usedKeys, setUsedKeys] = useState({})

    //format a guess into an array of letter Objects
    const formatGuess = () => {
        let solutionArray = [...solution]
        let formattedGuess = [...currentGuess].map((l) => {
            return {key: l, color: 'pink'}
        })

        //find any green letters
        formattedGuess.forEach((l, i) => {
            if(solutionArray[i] === l.key){
                formattedGuess[i].color = "purple"
                solutionArray[i] = null
            }
        })

        //find any yellow letter
        formattedGuess.forEach((l, i) => {
            if(solutionArray.includes(l.key) && l.color !== 'green'){
                formattedGuess[i].color = "lila"
                solutionArray[solutionArray.indexOf(l.key)] = null
            }
        })
        return formattedGuess
    }

    const addNewGuess = (formattedGuess) => {
        if(currentGuess === solution){
            setIsCorrect(true)
        }
        setGuesses((prevGuesses) => {
            let newGuesses = [...prevGuesses]
            newGuesses[turn] = formatGuess
            return newGuesses
        })
        setHistory((prevHistory) => {
            return [...prevHistory, currentGuess]
        })
        setTurn((prevTurn) => {
            return prevTurn + 1
        })
        setUsedKeys((prevUsedKeys)=>{
            let newKeys = {...prevUsedKeys}

            formattedGuess.forEach((l) =>{
                const currentColor = newKeys[l.key]

                if(l.color === 'green'){
                    newKeys[l.key] = 'green'
                    return
                }
                if(l.color === 'pink' && currentColor !== 'green'){
                    newKeys[l.key] = 'pink'
                    return
                }
                if(l.color === 'purple' && currentColor !== 'green' && currentColor !== 'pink'){
                    newKeys[l.key] = 'purple'
                    return
                }
            })
            return newKeys
        })
        setCurrentGuess('')
    }


    //tracking current guess, user input
    const handleKeyup = ({key}) => {
        if(key === 'Enter'){
            //only add guess if turn is less than 5
            if(turn > 5){
                console.log('you used all your guesses')
                return
            }
            // do not allow duplicate words
            if(history.includes(currentGuess)){
                console.log('you already tried that word')
                return
            }
            //check word is 5 chars word
            if(currentGuess.length !== 5){
                console.log('word muss be 5 char long')
                return
            }
            const formatted = formatGuess()
            addNewGuess(formatted)
        }
        if(key === 'Backspace'){
            setCurrentGuess((prev) => {
                return prev.slice(0, -1) //return new string based on old string, remove letter
            })
            return
        }
        if(/^[A-Za-z]$/.test(key)){
            if(currentGuess.length < 5){
                setCurrentGuess((prev) => {
                    return prev + key
                })
            }
        }
    }

    return{turn, currentGuess, guesses, isCorrect, handleKeyup}
}


export default useWordle
